// frontend/src/components/Dashboard/Sources/SourceForm.js
import React, { useState, useEffect } from 'react';

const SourceForm = ({ source, onSave, onCancel, mode }) => {
  const [formData, setFormData] = useState({
    name: '',
    type: 'private_foundation',
    category: '',
    deadline: '',
    amount: '',
    status: 'active',
    website: '',
    contactEmail: '',
    eligibility: '',
    focusAreas: [],
    notes: '',
    matchScore: 0
  });

  const [newFocusArea, setNewFocusArea] = useState('');

  const sourceTypes = [
    { value: 'government', label: 'Government Agency' },
    { value: 'private_foundation', label: 'Private Foundation' },
    { value: 'corporate', label: 'Corporate Giving' },
    { value: 'community', label: 'Community Fund' }
  ];

  const categories = [
    'Education',
    'Healthcare',
    'Environment',
    'Arts & Culture',
    'Community Development',
    'Social Justice',
    'STEM Research',
    'Youth Programs',
    'Senior Services',
    'Animal Welfare',
    'Disaster Relief',
    'International Development'
  ];

  const statusOptions = [
    { value: 'active', label: 'Active' },
    { value: 'upcoming', label: 'Upcoming' },
    { value: 'closed', label: 'Closed' }
  ];

  useEffect(() => {
    if (source) {
      setFormData({
        ...formData,
        ...source
      });
    }
  }, [source]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  const handleAddFocusArea = () => {
    if (newFocusArea.trim() && !formData.focusAreas.includes(newFocusArea.trim())) {
      setFormData({
        ...formData,
        focusAreas: [...formData.focusAreas, newFocusArea.trim()]
      });
      setNewFocusArea('');
    }
  };

  const handleRemoveFocusArea = (areaToRemove) => {
    setFormData({
      ...formData,
      focusAreas: formData.focusAreas.filter(area => area !== areaToRemove)
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleAddFocusArea();
    }
  };

  return (
    <div className="source-form-container">
      <div className="form-header">
        <button className="btn-back" onClick={onCancel}>
          <i className="fas fa-arrow-left"></i>
          Back to Sources
        </button>
        <h1>{mode === 'edit' ? 'Edit Grant Source' : 'Add Grant Source'}</h1>
        <p>{mode === 'edit' ? 'Update source information' : 'Add a new funding source to your database'}</p>
      </div>

      <form onSubmit={handleSubmit} className="source-form">
        {/* Basic Information */}
        <div className="form-section">
          <h2>Basic Information</h2>
          <div className="form-grid">
            <div className="form-group full-width">
              <label htmlFor="name">Source Name *</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                placeholder="e.g., National Science Foundation"
              />
            </div>

            <div className="form-group">
              <label htmlFor="type">Source Type *</label>
              <select
                id="type"
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
              >
                {sourceTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="category">Category *</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="status">Status *</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleChange}
                required
              >
                {statusOptions.map(status => (
                  <option key={status.value} value={status.value}>
                    {status.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="deadline">Application Deadline</label>
              <input
                type="date"
                id="deadline"
                name="deadline"
                value={formData.deadline}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="amount">Funding Range</label>
              <input
                type="text"
                id="amount"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                placeholder="e.g., $50,000 - $500,000"
              />
            </div>

            <div className="form-group">
              <label htmlFor="matchScore">Match Score</label>
              <input
                type="number"
                id="matchScore"
                name="matchScore"
                value={formData.matchScore}
                onChange={handleChange}
                min="0"
                max="100"
                placeholder="0-100"
              />
            </div>
          </div>
        </div>

        {/* Contact Information */}
        <div className="form-section">
          <h2>Contact Information</h2>
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="website">Website</label>
              <input
                type="url"
                id="website"
                name="website"
                value={formData.website}
                onChange={handleChange}
                placeholder="https://example.com"
              />
            </div>

            <div className="form-group">
              <label htmlFor="contactEmail">Contact Email</label>
              <input
                type="email"
                id="contactEmail"
                name="contactEmail"
                value={formData.contactEmail}
                onChange={handleChange}
                placeholder="grants@foundation.org"
              />
            </div>

            <div className="form-group full-width">
              <label htmlFor="eligibility">Eligibility Requirements</label>
              <textarea
                id="eligibility"
                name="eligibility"
                value={formData.eligibility}
                onChange={handleChange}
                rows="3"
                placeholder="Describe eligibility requirements..."
              />
            </div>
          </div>
        </div>

        {/* Focus Areas */}
        <div className="form-section">
          <h2>Focus Areas</h2>
          <div className="form-group full-width">
            <label>Focus Areas & Priorities</label>
            <div className="tags-input">
              <div className="tags-list">
                {formData.focusAreas.map((area, index) => (
                  <span key={index} className="tag">
                    {area}
                    <button
                      type="button"
                      onClick={() => handleRemoveFocusArea(area)}
                      className="tag-remove"
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  </span>
                ))}
              </div>
              <div className="tag-input-wrapper">
                <input
                  type="text"
                  value={newFocusArea}
                  onChange={(e) => setNewFocusArea(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Add a focus area and press Enter"
                />
                <button type="button" onClick={handleAddFocusArea} className="btn-tag-add">
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Additional Information */}
        <div className="form-section">
          <h2>Additional Information</h2>
          <div className="form-group full-width">
            <label htmlFor="notes">Notes & Comments</label>
            <textarea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="4"
              placeholder="Add any additional information, special requirements, or comments about this funding source..."
            />
          </div>
        </div>

        {/* Form Actions */}
        <div className="form-actions">
          <button type="button" className="btn btn-outline" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="btn btn-primary">
            {mode === 'edit' ? 'Update Source' : 'Add Source'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default SourceForm;
