// frontend/src/components/Dashboard/Sources/Sources.js
import React, { useState, useEffect } from 'react';
import SourceList from './SourceList';
import SourceDetails from './SourceDetails';
import SourceForm from './SourceForm';
import './Sources.css';

const Sources = () => {
  const [view, setView] = useState('list'); // 'list', 'details', 'create', 'edit'
  const [selectedSource, setSelectedSource] = useState(null);
  const [sources, setSources] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  // Mock data - replace with actual API calls
  useEffect(() => {
    const mockSources = [
      {
        id: '1',
        name: 'National Science Foundation',
        type: 'government',
        category: 'STEM Education',
        deadline: '2024-03-15',
        amount: '500,000 - 2,000,000',
        status: 'active',
        matchScore: 95,
        website: 'https://www.nsf.gov',
        contactEmail: 'grants@nsf.gov',
        eligibility: 'Universities, Research Institutions',
        focusAreas: ['STEM Education', 'Research', 'Technology'],
        notes: 'Focus on innovative STEM education programs',
        lastUpdated: '2024-01-15'
      },
      {
        id: '2',
        name: 'Ford Foundation',
        type: 'private_foundation',
        category: 'Social Justice',
        deadline: '2024-06-30',
        amount: '100,000 - 1,000,000',
        status: 'active',
        matchScore: 88,
        website: 'https://www.fordfoundation.org',
        contactEmail: 'grants@fordfoundation.org',
        eligibility: 'Nonprofits, Community Organizations',
        focusAreas: ['Social Justice', 'Equity', 'Community Development'],
        notes: 'Priority given to organizations serving marginalized communities',
        lastUpdated: '2024-01-10'
      },
      {
        id: '3',
        name: 'Department of Energy',
        type: 'government',
        category: 'Clean Energy',
        deadline: '2024-04-20',
        amount: '750,000 - 5,000,000',
        status: 'active',
        matchScore: 92,
        website: 'https://www.energy.gov',
        contactEmail: 'funding@energy.gov',
        eligibility: 'Research Institutions, Energy Companies',
        focusAreas: ['Renewable Energy', 'Climate Change', 'Innovation'],
        notes: 'Focus on clean energy research and development',
        lastUpdated: '2024-01-08'
      },
      {
        id: '4',
        name: 'Gates Foundation',
        type: 'private_foundation',
        category: 'Global Health',
        deadline: '2024-05-15',
        amount: '500,000 - 10,000,000',
        status: 'active',
        matchScore: 85,
        website: 'https://www.gatesfoundation.org',
        contactEmail: 'grants@gatesfoundation.org',
        eligibility: 'Global Health Organizations, Research Institutions',
        focusAreas: ['Global Health', 'Education', 'Poverty Alleviation'],
        notes: 'Focus on global health initiatives and education',
        lastUpdated: '2024-01-05'
      },
      {
        id: '5',
        name: 'National Endowment for the Arts',
        type: 'government',
        category: 'Arts & Culture',
        deadline: '2024-02-28',
        amount: '10,000 - 100,000',
        status: 'upcoming',
        matchScore: 78,
        website: 'https://www.arts.gov',
        contactEmail: 'grants@arts.gov',
        eligibility: 'Arts Organizations, Individual Artists',
        focusAreas: ['Arts Education', 'Cultural Programs', 'Community Arts'],
        notes: 'Supports arts education and community cultural programs',
        lastUpdated: '2024-01-12'
      }
    ];
    
    setSources(mockSources);
    setLoading(false);
  }, []);

  const handleViewSource = (source) => {
    setSelectedSource(source);
    setView('details');
  };

  const handleEditSource = (source) => {
    setSelectedSource(source);
    setView('edit');
  };

  const handleCreateSource = () => {
    setSelectedSource(null);
    setView('create');
  };

  const handleSaveSource = (sourceData) => {
    if (sourceData.id) {
      setSources(prev => prev.map(source => 
        source.id === sourceData.id ? sourceData : source
      ));
    } else {
      const newSource = {
        ...sourceData,
        id: Date.now().toString()
      };
      setSources(prev => [...prev, newSource]);
    }
    setView('list');
  };

  const handleDeleteSource = (sourceId) => {
    setSources(prev => prev.filter(source => source.id !== sourceId));
    setView('list');
  };

  const handleBackToList = () => {
    setView('list');
    setSelectedSource(null);
  };

  const filteredSources = sources.filter(source => {
    if (filter === 'all') return true;
    return source.status === filter;
  });

  if (loading) {
    return (
      <div className="sources-loading">
        <i className="fas fa-spinner"></i>
        <p>Loading grant sources...</p>
      </div>
    );
  }

  return (
    <div className="sources-container">
      {view === 'list' && (
        <SourceList
          sources={filteredSources}
          onViewSource={handleViewSource}
          onEditSource={handleEditSource}
          onCreateSource={handleCreateSource}
          onDeleteSource={handleDeleteSource}
          filter={filter}
          onFilterChange={setFilter}
        />
      )}

      {view === 'details' && selectedSource && (
        <SourceDetails
          source={selectedSource}
          onBack={handleBackToList}
          onEdit={() => setView('edit')}
        />
      )}

      {(view === 'create' || view === 'edit') && (
        <SourceForm
          source={view === 'edit' ? selectedSource : null}
          onSave={handleSaveSource}
          onCancel={handleBackToList}
          mode={view}
        />
      )}
    </div>
  );
};

export default Sources;
