import React, { useState, useEffect } from 'react';

const GrantForm = ({ grant, clients, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    clientId: '',
    funder: '',
    amount: '',
    deadline: '',
    status: 'draft',
    category: '',
    priority: 'medium',
    tags: [],
    notes: ''
  });
  const [newTag, setNewTag] = useState('');

  useEffect(() => {
    if (grant) {
      setFormData({
        title: grant.title || '',
        clientId: grant.clientId || '',
        funder: grant.funder || '',
        amount: grant.amount || '',
        deadline: grant.deadline || '',
        status: grant.status || 'draft',
        category: grant.category || '',
        priority: grant.priority || 'medium',
        tags: grant.tags || [],
        notes: grant.notes || ''
      });
    }
  }, [grant]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleAddTag = () => {
    if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
      setFormData({
        ...formData,
        tags: [...formData.tags, newTag.trim()]
      });
      setNewTag('');
    }
  };

  const handleRemoveTag = (tagToRemove) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter(tag => tag !== tagToRemove)
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleAddTag();
    }
  };

  const categories = [
    'Education', 'Healthcare', 'Environment', 'Technology', 
    'Arts & Culture', 'Community Development', 'Youth', 
    'Research', 'Social Services', 'Other'
  ];

  return (
    <div className="grant-form-container">
      <div className="form-header">
        <h1>{grant ? 'Edit Grant' : 'Create New Grant'}</h1>
        <p>{grant ? 'Update grant information' : 'Start a new grant application'}</p>
      </div>

      <form onSubmit={handleSubmit} className="grant-form">
        <div className="form-grid">
          <div className="form-group full-width">
            <label htmlFor="title">Grant Title *</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              placeholder="Enter grant title"
            />
          </div>

          <div className="form-group">
            <label htmlFor="clientId">Client *</label>
            <select
              id="clientId"
              name="clientId"
              value={formData.clientId}
              onChange={handleChange}
              required
            >
              <option value="">Select Client</option>
              {clients.map(client => (
                <option key={client.id} value={client.id}>
                  {client.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="funder">Funding Organization *</label>
            <input
              type="text"
              id="funder"
              name="funder"
              value={formData.funder}
              onChange={handleChange}
              required
              placeholder="Enter funder name"
            />
          </div>

          <div className="form-group">
            <label htmlFor="amount">Funding Amount *</label>
            <input
              type="text"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              required
              placeholder="e.g., $500,000"
            />
          </div>

          <div className="form-group">
            <label htmlFor="deadline">Application Deadline *</label>
            <input
              type="date"
              id="deadline"
              name="deadline"
              value={formData.deadline}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="category">Category</label>
            <select
              id="category"
              name="category"
              value={formData.category}
              onChange={handleChange}
            >
              <option value="">Select Category</option>
              {categories.map(category => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="status">Status</label>
            <select
              id="status"
              name="status"
              value={formData.status}
              onChange={handleChange}
            >
              <option value="draft">Draft</option>
              <option value="in_review">In Review</option>
              <option value="submitted">Submitted</option>
              <option value="approved">Approved</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="priority">Priority</label>
            <select
              id="priority"
              name="priority"
              value={formData.priority}
              onChange={handleChange}
            >
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>
          </div>

          <div className="form-group full-width">
            <label htmlFor="tags">Tags</label>
            <div className="tags-input">
              <div className="tags-list">
                {formData.tags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                    <button
                      type="button"
                      onClick={() => handleRemoveTag(tag)}
                      className="tag-remove"
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  </span>
                ))}
              </div>
              <div className="tag-input-wrapper">
                <input
                  type="text"
                  value={newTag}
                  onChange={(e) => setNewTag(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Add a tag and press Enter"
                />
                <button type="button" onClick={handleAddTag} className="btn-tag-add">
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
          </div>

          <div className="form-group full-width">
            <label htmlFor="notes">Notes & Requirements</label>
            <textarea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="4"
              placeholder="Add any notes, requirements, or special instructions..."
            />
          </div>
        </div>

        <div className="form-actions">
          <button type="button" className="btn btn-outline" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="btn btn-primary">
            {grant ? 'Update Grant' : 'Create Grant'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default GrantForm;
