import React, { useState, useEffect } from 'react';
import ClientList from './ClientList';
import ClientForm from './ClientForm';
import ClientDetails from './ClientDetails';
import BulkEmail from './BulkEmail';
import EmailComposer from './EmailComposer';
import ClientCommunication from './ClientCommunication';
import CommunicationHistory from './CommunicationHistory';
import EmailTemplates from './EmailTemplates';
import './Clients.css';

const Clients = () => {
  const [view, setView] = useState('list'); // 'list', 'form', 'details', 'communication', 'history', 'templates', 'bulk-email'
  const [selectedClient, setSelectedClient] = useState(null);
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [communicationTab, setCommunicationTab] = useState('emails'); // 'emails', 'calls', 'meetings', 'notes'

  // Mock data - Replace with API calls
  useEffect(() => {
    const mockClients = [
      {
        id: 1,
        name: 'GreenTech Initiative',
        email: 'contact@greentech.org',
        phone: '+1 (555) 123-4567',
        organization: 'GreenTech Initiative',
        status: 'active',
        lastContact: '2024-01-15',
        grantsSubmitted: 12,
        grantsAwarded: 8,
        totalFunding: '$450,000',
        avatar: 'https://i.pravatar.cc/150?img=1',
        notes: 'Very responsive and organized. Great partnership potential.',
        tags: ['Environment', 'Technology', 'Non-Profit'],
        communicationHistory: [
          {
            id: 1,
            type: 'email',
            direction: 'outgoing',
            subject: 'Grant Proposal Feedback',
            preview: 'Thank you for submitting the draft proposal...',
            date: '2024-01-15T10:30:00',
            status: 'sent',
            important: true
          },
          {
            id: 2,
            type: 'call',
            direction: 'incoming',
            subject: 'Follow-up call',
            preview: 'Discussed next steps for the NSF grant...',
            date: '2024-01-12T14:20:00',
            duration: '15m',
            status: 'completed'
          }
        ]
      },
      {
        id: 2,
        name: 'Sarah Chen',
        email: 'sarah.chen@communityhealth.org',
        phone: '+1 (555) 987-6543',
        organization: 'Community Health Alliance',
        status: 'active',
        lastContact: '2024-01-10',
        grantsSubmitted: 8,
        grantsAwarded: 5,
        totalFunding: '$280,000',
        avatar: 'https://i.pravatar.cc/150?img=32',
        notes: 'Focuses on healthcare access in underserved communities.',
        tags: ['Healthcare', 'Community', 'Non-Profit'],
        communicationHistory: [
          {
            id: 1,
            type: 'email',
            direction: 'incoming',
            subject: 'Question about budget',
            preview: 'Could you clarify the budget allocation for...',
            date: '2024-01-10T09:15:00',
            status: 'read',
            important: false
          }
        ]
      },
      {
        id: 3,
        name: 'Michael Rodriguez',
        email: 'm.rodriguez@youthfuture.org',
        phone: '+1 (555) 456-7890',
        organization: 'Youth Future Foundation',
        status: 'inactive',
        lastContact: '2023-12-20',
        grantsSubmitted: 15,
        grantsAwarded: 10,
        totalFunding: '$620,000',
        avatar: 'https://i.pravatar.cc/150?img=8',
        notes: 'Excellent track record with education grants.',
        tags: ['Education', 'Youth', 'Foundation'],
        communicationHistory: []
      }
    ];
    
    setClients(mockClients);
    setLoading(false);
  }, []);

  const handleAddClient = () => {
    setSelectedClient(null);
    setView('form');
  };

  const handleEditClient = (client) => {
    setSelectedClient(client);
    setView('form');
  };

  const handleViewClient = (client) => {
    setSelectedClient(client);
    setView('details');
  };

  const handleCommunication = (client) => {
    setSelectedClient(client);
    setView('communication');
  };

  const handleViewHistory = (client) => {
    setSelectedClient(client);
    setView('history');
  };

  const handleSaveClient = (clientData) => {
    if (selectedClient) {
      // Update existing client
      setClients(clients.map(client => 
        client.id === selectedClient.id 
          ? { ...client, ...clientData }
          : client
      ));
    } else {
      // Add new client
      const newClient = {
        ...clientData,
        id: Date.now(),
        grantsSubmitted: 0,
        grantsAwarded: 0,
        totalFunding: '$0',
        avatar: `https://i.pravatar.cc/150?img=${Math.floor(Math.random() * 70)}`,
        communicationHistory: []
      };
      setClients([...clients, newClient]);
    }
    setView('list');
  };

  const handleDeleteClient = (clientId) => {
    if (window.confirm('Are you sure you want to delete this client?')) {
      setClients(clients.filter(client => client.id !== clientId));
    }
  };

  const handleBulkEmail = () => {
    setView('bulk-email');
  };

  const handleSendEmail = (client) => {
    setSelectedClient(client);
    setView('email-composer');
  };

  const handleViewTemplates = () => {
    setView('templates');
  };

  const handleAddCommunication = (communication) => {
    if (selectedClient) {
      const updatedClients = clients.map(client => {
        if (client.id === selectedClient.id) {
          return {
            ...client,
            communicationHistory: [
              ...client.communicationHistory,
              {
                ...communication,
                id: Date.now(),
                date: new Date().toISOString()
              }
            ]
          };
        }
        return client;
      });
      setClients(updatedClients);
      setSelectedClient(updatedClients.find(c => c.id === selectedClient.id));
    }
  };

  const filteredClients = clients.filter(client =>
    client.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    client.organization.toLowerCase().includes(searchTerm.toLowerCase()) ||
    client.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const renderView = () => {
    switch (view) {
      case 'list':
        return (
          <ClientList
            clients={filteredClients}
            loading={loading}
            onAddClient={handleAddClient}
            onEditClient={handleEditClient}
            onViewClient={handleViewClient}
            onDeleteClient={handleDeleteClient}
            onSendEmail={handleSendEmail}
            onBulkEmail={handleBulkEmail}
            onCommunication={handleCommunication}
            onViewHistory={handleViewHistory}
            onViewTemplates={handleViewTemplates}
            searchTerm={searchTerm}
            onSearchChange={setSearchTerm}
          />
        );
      case 'form':
        return (
          <ClientForm
            client={selectedClient}
            onSave={handleSaveClient}
            onCancel={() => setView('list')}
          />
        );
      case 'details':
        return (
          <ClientDetails
            client={selectedClient}
            onEdit={() => handleEditClient(selectedClient)}
            onBack={() => setView('list')}
            onSendEmail={() => handleSendEmail(selectedClient)}
            onCommunication={() => handleCommunication(selectedClient)}
            onViewHistory={() => handleViewHistory(selectedClient)}
          />
        );
      case 'communication':
        return (
          <ClientCommunication
            client={selectedClient}
            onSendEmail={() => handleSendEmail(selectedClient)}
            onAddCommunication={handleAddCommunication}
            onBack={() => setView('list')}
            activeTab={communicationTab}
            onTabChange={setCommunicationTab}
          />
        );
      case 'history':
        return (
          <CommunicationHistory
            client={selectedClient}
            onBack={() => setView('list')}
            onSendEmail={() => handleSendEmail(selectedClient)}
          />
        );
      case 'templates':
        return (
          <EmailTemplates
            onBack={() => setView('list')}
            onUseTemplate={(template) => {
              setSelectedClient(null);
              setView('email-composer');
            }}
          />
        );
      case 'bulk-email':
        return (
          <BulkEmail
            clients={clients}
            onSend={() => setView('list')}
            onCancel={() => setView('list')}
          />
        );
      case 'email-composer':
        return (
          <EmailComposer
            client={selectedClient}
            onSend={() => setView('list')}
            onCancel={() => setView('list')}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="clients-container">
      {renderView()}
    </div>
  );
};

export default Clients;
