// frontend/src/components/Dashboard/Submissions/SubmissionDetails.js
import React from 'react';
import SubmissionTimeline from './SubmissionTimeline';

const SubmissionDetails = ({ submission, onBack, onEdit }) => {
  const formatDate = (dateString) => {
    if (!dateString) return 'Not specified';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'draft': return 'status-badge status-draft';
      case 'submitted': return 'status-badge status-submitted';
      case 'approved': return 'status-badge status-approved';
      case 'rejected': return 'status-badge status-rejected';
      default: return 'status-badge status-draft';
    }
  };

  return (
    <div className="submission-details">
      <div className="details-header">
        <button className="btn-back" onClick={onBack}>
          <i className="fas fa-arrow-left"></i>
          Back to Submissions
        </button>
        
        <div className="header-actions">
          <button className="btn-edit" onClick={onEdit}>
            <i className="fas fa-edit"></i>
            Edit Submission
          </button>
        </div>
      </div>

      <div className="details-content">
        <div className="details-main">
          {/* Submission Overview */}
          <div className="details-section">
            <h2>Submission Overview</h2>
            <div className="info-grid">
              <div className="info-item">
                <label>Grant Title</label>
                <p className="info-value">{submission.grantTitle}</p>
              </div>
              <div className="info-item">
                <label>Client Organization</label>
                <p className="info-value">{submission.clientName}</p>
              </div>
              <div className="info-item">
                <label>Funding Organization</label>
                <p className="info-value">{submission.funder}</p>
              </div>
              <div className="info-item">
                <label>Requested Amount</label>
                <p className="info-value amount">{formatCurrency(submission.amount)}</p>
              </div>
              <div className="info-item">
                <label>Submission Status</label>
                <p>
                  <span className={getStatusBadgeClass(submission.status)}>
                    {submission.status}
                  </span>
                </p>
              </div>
              <div className="info-item">
                <label>Submitted Date</label>
                <p className="info-value">{formatDate(submission.submittedDate)}</p>
              </div>
              <div className="info-item">
                <label>Decision Date</label>
                <p className="info-value">{formatDate(submission.decisionDate)}</p>
              </div>
            </div>
          </div>

          {/* Notes & Additional Information */}
          <div className="details-section">
            <h2>Notes & Additional Information</h2>
            <div className="notes-content">
              <p>{submission.notes || 'No additional notes provided.'}</p>
            </div>
          </div>

          {/* Attached Documents */}
          <div className="details-section">
            <h2>Attached Documents</h2>
            <div className="documents-list">
              {submission.documents && submission.documents.length > 0 ? (
                submission.documents.map((doc, index) => (
                  <div key={index} className="document-item">
                    <i className="fas fa-file-pdf document-icon"></i>
                    <div className="document-info">
                      <div className="document-name">{doc.name}</div>
                      <div className="document-type">{doc.type}</div>
                    </div>
                    <button className="btn-download">
                      <i className="fas fa-download"></i>
                    </button>
                  </div>
                ))
              ) : (
                <p className="no-documents">No documents attached to this submission.</p>
              )}
            </div>
          </div>
        </div>

        {/* Timeline Sidebar */}
        <div className="details-sidebar">
          <SubmissionTimeline submission={submission} />
        </div>
      </div>
    </div>
  );
};

export default SubmissionDetails;
