import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import './Login.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const { login, register } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      let result;
      if (isLogin) {
        result = await login(formData.email, formData.password);
      } else {
        result = await register(formData.name, formData.email, formData.password);
      }

      if (result.success) {
        setMessage('Success! Redirecting...');
      } else {
        setMessage(result.message);
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fillDemoCredentials = (email, password) => {
    setFormData({
      ...formData,
      email,
      password
    });
  };

  return (
    <div className="login-container">
      {/* Left Side - Login Form */}
      <div className="login-form-section">
        <div className="login-form-container">
          <div className="login-header">
            <div className="logo">
              <i className="fas fa-hand-holding-usd"></i>
              <span className="logo-text">GrantFlow</span>
            </div>
            <h1>Welcome Back</h1>
            <p>Sign in to your GrantFlow account</p>
          </div>

          {/* Demo Credentials */}
          <div className="demo-credentials">
            <div className="demo-header">
              <i className="fas fa-key"></i>
              <span>Demo Access</span>
            </div>
            <div className="demo-buttons">
              <button 
                type="button" 
                className="demo-btn"
                onClick={() => fillDemoCredentials('demo@grantflow.com', 'demo123')}
              >
                <i className="fas fa-user"></i>
                Demo User
              </button>
              <button 
                type="button" 
                className="demo-btn"
                onClick={() => fillDemoCredentials('sarah.johnson@grantflow.com', 'password123')}
              >
                <i className="fas fa-user-tie"></i>
                Sarah Johnson
              </button>
            </div>
          </div>

          <form className="login-form" onSubmit={handleSubmit}>
            {!isLogin && (
              <div className="form-group">
                <label htmlFor="name">
                  <i className="fas fa-user"></i>
                  Full Name
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  className="form-control"
                  placeholder="Enter your full name"
                  value={formData.name}
                  onChange={handleChange}
                  required={!isLogin}
                />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="email">
                <i className="fas fa-envelope"></i>
                Email Address
              </label>
              <input
                type="email"
                id="email"
                name="email"
                className="form-control"
                placeholder="Enter your email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">
                <i className="fas fa-lock"></i>
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                className="form-control"
                placeholder="Enter your password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>

            {message && (
              <div className={`message ${message.includes('Success') ? 'success' : 'error'}`}>
                <i className={`fas ${message.includes('Success') ? 'fa-check-circle' : 'fa-exclamation-circle'}`}></i>
                {message}
              </div>
            )}

            <button 
              type="submit" 
              className="login-btn" 
              disabled={loading}
            >
              {loading ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i>
                  Processing...
                </>
              ) : (
                <>
                  <i className={`fas ${isLogin ? 'fa-sign-in-alt' : 'fa-user-plus'}`}></i>
                  {isLogin ? 'Sign In' : 'Create Account'}
                </>
              )}
            </button>

            <div className="login-footer">
              <p>
                {isLogin ? "Don't have an account? " : "Already have an account? "}
                <button 
                  type="button" 
                  className="link-btn"
                  onClick={() => {
                    setIsLogin(!isLogin);
                    setMessage('');
                  }}
                >
                  {isLogin ? 'Create one here' : 'Sign in here'}
                </button>
              </p>
            </div>
          </form>
        </div>

        {/* Watermark */}
        <div className="watermark">
          Tool Made with <span className="heart">❤️</span> by NeonByteAI
        </div>
      </div>

      {/* Right Side - Company Messaging */}
      <div className="company-section">
        <div className="company-content">
          <div className="company-header">
            <h2>Transform Your Grant Management</h2>
            <div className="accent-line"></div>
          </div>
          
          <div className="features-list">
            <div className="feature-item">
              <div className="feature-icon">
                <i className="fas fa-robot"></i>
              </div>
              <div className="feature-content">
                <h3>AI-Powered Grant Writing</h3>
                <p>Generate compelling grant proposals in minutes with our advanced AI assistant</p>
              </div>
            </div>

            <div className="feature-item">
              <div className="feature-icon">
                <i className="fas fa-bolt"></i>
              </div>
              <div className="feature-content">
                <h3>Smart Grant Matching</h3>
                <p>Automatically find the perfect funding opportunities for your organization</p>
              </div>
            </div>

            <div className="feature-item">
              <div className="feature-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <div className="feature-content">
                <h3>Real-time Analytics</h3>
                <p>Track your grant performance and success rates with detailed insights</p>
              </div>
            </div>

            <div className="feature-item">
              <div className="feature-icon">
                <i className="fas fa-users"></i>
              </div>
              <div className="feature-content">
                <h3>Collaborative Workflows</h3>
                <p>Work seamlessly with your team on grant applications and submissions</p>
              </div>
            </div>
          </div>

          <div className="stats">
            <div className="stat">
              <div className="stat-number">$2.1B+</div>
              <div className="stat-label">Funding Secured</div>
            </div>
            <div className="stat">
              <div className="stat-number">15K+</div>
              <div className="stat-label">Successful Grants</div>
            </div>
            <div className="stat">
              <div className="stat-number">98%</div>
              <div className="stat-label">Client Satisfaction</div>
            </div>
          </div>

          <div className="testimonial">
            <div className="quote">
              "GrantFlow helped us secure $500K in funding we never would have found on our own. The AI writing assistant is a game-changer!"
            </div>
            <div className="author">
              <div className="author-avatar">
                <img src="https://i.pravatar.cc/150?img=32" alt="Sarah Chen" />
              </div>
              <div className="author-info">
                <div className="author-name">Sarah Chen</div>
                <div className="author-role">Executive Director, GreenTech Initiative</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
